//select all divs and give a purple background 
$("div").css("background", "purple");
//select the divs with class highlight and make them 200px wide 
$(".highlight").css("width", "200px");
//select the divs with id "third" and give it an orange border 
$("#third").css("border", "4px solid orange");
//select the first div only and change its font color to pink 
$("div:first").css("color", "pink");